import { createAction, handleActions } from "redux-actions"; 
// redux-actions 라이브러리에서 createAction(액션 생성자), handleActions(리듀서 생성 도우미) 불러옴

// 액션 타입 상수 선언
const SAMPLE_ACTION = "auth/SAMPLE_ACTION"; 
// 'auth' 모듈의 SAMPLE_ACTION 이라는 액션 이름 (네임스페이스처럼 구분)

// 액션 생성자 정의
export const sampleAction = createAction(SAMPLE_ACTION); 
// payload를 자동으로 담아서 만들어주는 액션 생성 함수

// 초기 상태
const initialState = {
    sample: null, // sample 상태는 기본적으로 null
};

// 리듀서 정의
const auth = handleActions(
    {
        // SAMPLE_ACTION 이 들어오면 실행될 리듀서
        [SAMPLE_ACTION]: (state, action) => ({
            ...state, // 기존 상태를 복사하고
            sample: action.payload, // sample 필드만 새로 업데이트
        }),
    },
    initialState // 초기 상태 지정
);

export default auth; // 이 auth 리듀서를 모듈에서 기본 export
