import styled from "styled-components"; // styled-components를 불러와서 CSS-in-JS 작성
import palette from "../../lib/styles/palette"; // 색상 팔레트(사용자 정의 색상 모음) 불러오기
import { css } from "styled-components";

// StyledButton: 실제로 스타일이 적용될 HTML button
const StyledButton = styled.button`
    border: none; // 테두리 없애기
    border-radius: 4px; // 모서리 둥글게
    font-size: 1rem; // 글자 크기
    font-weight: bold; // 글자 굵게
    padding: 0.25rem 1rem; // 위아래 0.25rem, 좌우 1rem
    color: white; // 글자색 흰색
    outline: none; // 포커스 아웃라인 제거
    cursor: pointer; // 마우스 올리면 포인터 모양으로

    background: ${palette.gray[8]}; // 배경색: 사용자 정의 팔레트의 진회색
    &:hover {
        background: ${palette.gray[6]}; // 마우스 올리면 약간 밝은 회색으로 변경
    }

    // props에 fullWidth가 true면 적용되는 스타일
    ${props =>
        props.fullWidth &&
        css`
            padding-top: 0.75rem; // 위 여백 넉넉하게
            padding-bottom: 0.75rem; // 아래 여백 넉넉하게
            width: 100%; // 가로폭 100% (꽉 차게)
            font-size: 1.125rem; // 글자 크기 좀 더 키움
        `}

    ${props =>
        props.cyan &&
        css`
            background: ${palette.cyan[5]};
            &:hover {
                background: ${palette.cyan[4]};
            }
        `}
`;

// Button: 실제로 사용하는 컴포넌트
const Button = ({ children, ...rest }) => {
    // StyledButton 에 props를 그대로 전달
    return <StyledButton {...rest}>{children}</StyledButton>;
};

export default Button; // Button 컴포넌트를 기본으로 내보냄
