import styled from "styled-components";
import WhiteBox from "../common/WhiteBox";
import { Link } from 'react-router-dom';
import palette from "../../lib/styles/palette";

// styled-components 라이브러리 import
// → CSS-in-JS 스타일을 작성하기 위해 사용

const AuthTemplateBlock = styled.div`
    position: absolute; // 화면 전체를 덮도록 위치 고정
    left: 0; // 왼쪽 끝부터
    top: 0;  // 위쪽 끝부터
    bottom: 0; // 아래쪽 끝까지
    right: 0;  // 오른쪽 끝까지
    background: ${palette.gray[2]}
    display: flex; // 내부 요소를 flex 레이아웃으로 배치
    flex-direction: column; // 위에서 아래로 쌓이도록 세로 방향
    justify-content: center;
    align-items: center;
`;
// → 즉, 화면을 꽉 채우는 flex 컨테이너 정의



const AuthTemplate = ({ children }) => {
    // AuthTemplate 컴포넌트
    // children → 내부에 들어올 요소들을 props로 받음
    return (
        <AuthTemplateBlock>
            <WhiteBox>
                <div className="logo-area">
                    <Link to="/">REACTERS</Link>
                </div>
                {children}
            </WhiteBox>
        </AuthTemplateBlock>
    );
};
// → AuthTemplateBlock 안에 WhiteBox를 두고
//    그 안에 children을 표시하는 구조

export default AuthTemplate;
// 다른 곳에서 AuthTemplate 컴포넌트를 import 할 수 있도록 export
