import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { changeField, initializeForm, register } from "../../modules/auth";
import AuthForm from "../../components/auth/AuthForm";
import { check } from "../../modules/user";

const RegisterForm = () => {
    const dispatch = useDispatch();
    const form = useSelector(state => state.auth.register, state.user.user);
    const auth = useSelector(state => state.auth.auth, state.user.user);
    const authError = useSelector(state => state.auth.authError, state.user.user);
    // 인풋 변경 이벤트 핸들러
    const onChange = e => {
        const { value, name } = e.target;
        dispatch(changeField('register', name, value));
    };
    // 폼 등록 이벤트 핸들러
    const onSubmit = e => {
        e.preventDefault();
        const { username, password, passwordConfirm } = form;
        if (password !== passwordConfirm) {
            //TODO: 에러처리리
            return;
        }
        dispatch(register({ username, password }));
    }
    // 컴포넌트에서 처음 렌더링 될 때 form 초기화
    useEffect(() => {
        dispatch(initializeForm('register'));
    }, [dispatch]);

    //회원가입 성공/실패처리
    useEffect(() => {
        if (authError) {
            console.log('회원가입 실패');
            console.log(authError);
        }
        if (auth) {
            console.log('회원가입 성공');
            console.log(auth);
            return;
        }
    }, [authError, auth]);

    return <AuthForm type="register" form={form} onChange={onChange} onSubmit={onSubmit} />;
};

export default RegisterForm;
